import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import UniversityAccordion from "./UniversityAccordion";

const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [uopSchools, setUopSchools] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedMajor, setSelectedMajor] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredUniversities, setFilteredUniversities] = useState([]);





const isSearchDisabled = !(
  selectedCountry ||
  selectedInstitution ||
  selectedSubjectArea ||
  selectedMajor ||
  selectedPacificSubjectArea ||
  pacificCourseCode

);

  const [searchClicked, setSearchClicked] = useState(false);
  const [showResults, setShowResults] = useState(false);


const dropdownStyle = {
  width: "100%",
  maxWidth: "500px",
  padding: "14px",
  borderRadius: "6px",
  border: "1px solid #ccc",
  fontSize: "17px",
  lineHeight: "1.6",
  marginBottom: "20px",
  backgroundColor: "#fff",
  backgroundImage: `url("data:image/svg+xml;utf8,<svg fill='%23232323' height='24' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><path d='M7 10l5 5 5-5z'/></svg>")`,
  backgroundRepeat: "no-repeat",
  backgroundPosition: "right 12px center",
  backgroundSize: "16px",
  appearance: "none",        // suppress native arrow
  WebkitAppearance: "none",
};

  const handleClear = () => {
    setSelectedCountry("");
    setSelectedInstitution("");
    setSelectedSubjectArea("");
    setSelectedMajor("");
    setSelectedPacificSubjectArea("");
    setPacificCourseCode("");
    setResults([]);
    setShowTooltip(false);
  };

useEffect(() => {
  const loadAll = async () => {
    try {
      const majorsRes = await fetch("/uop_majors.json");
      const majorsData = await majorsRes.json();
      setUopSchools(majorsData.majors); 

      const pacificSubjectsRes = await fetch("/subjects.json");



//  Load international course data
const courseEquivalentsData = await equivalentsRes.json();
const equivalentsRes = await fetch("/international_universities.json");

      //  Set schools and subject areas
      setUopSchools(schoolsData.schools);


      // Only keep universities with courses > 1
      const universitiesWithCourses = courseEquivalentsData.universities.filter(
        (uni) => Array.isArray(uni.courses) && uni.courses.length > 1
      );

      //  Flatten courses from these universities
      const flatCourses = universitiesWithCourses.flatMap((uni) =>
        uni.courses.map((course) => ({
          ...course,
          partner_university: uni.partner_university,
          country: uni.country,
        }))
      );

      // ‚úÖ Set all courses to be shown in table
      setAllCourses(flatCourses);

      // ‚úÖ Set dropdown options (unique values only)
      const uniqueCountries = [
        ...new Set(universitiesWithCourses.map((u) => u.country))
      ].sort();

      const uniqueInstitutions = [
        ...new Set(universitiesWithCourses.map((u) => u.partner_university))
      ].sort();


 setUopSchools(majorsData.majors);
      setCountries(uniqueCountries);
      setInstitutions(uniqueInstitutions);

    } catch (error) {
      console.error(" Error loading course data:", error);
    } finally {
      setLoading(false);
    }
  };

  loadAll();
}, []);


useEffect(() => {
  if (selectedCountry && allCourses.length > 0) {
    const matchedUnis = allCourses.filter(
      (course) => course.country === selectedCountry
    );

    const uniMap = new Map();

    matchedUnis.forEach((entry) => {
      const key = `${entry.partner_university}_${entry.country}`;

      if (!uniMap.has(key)) {
        uniMap.set(key, {
          partner_university: entry.partner_university,
          country: entry.country,
          courses: [], // initialize empty array
        });
      }

      // Push this course to the corresponding university
      uniMap.get(key).courses.push(entry);
    });

    setFilteredUniversities(Array.from(uniMap.values()));
  } else {
    setFilteredUniversities([]);
  }
}, [selectedCountry, allCourses]);


const handleFilter = () => {
  const filtered = allCourses.filter((course) => {
    return (
(!selectedCountry || course.country === selectedCountry) &&
    (!selectedInstitution || course.partner_university?.trim() === selectedInstitution.trim()) &&
    (!selectedSubjectArea || course.partner_course_subject === selectedSubjectArea) &&
    (!selectedPacificSubjectArea || course.pacific_subject_area === selectedPacificSubjectArea) &&
    (!pacificCourseCode || course.pacific_course_code?.toLowerCase().includes(pacificCourseCode.toLowerCase()))

    );
  });

const grouped = filtered.reduce((acc, course) => {
  const key = `${course.partner_university}__${course.country}`;

  if (!acc[key]) {
    acc[key] = {
      partner_university: course.partner_university,
      country: course.country,
      courses: [],
    };
  }

  // ‚úÖ Only push valid course objects
  if (!course.noCourses) {
    acc[key].courses.push(course);
  }

  return acc;
}, {});



 const groupedArray = Object.values(grouped);
setResults(groupedArray);         // ‚úÖ Sent to <UniversityAccordion />
setSearchClicked(true);
setShowResults(true);
};


 const tableHeaderStyle = {
  padding: "6px 8px",
  backgroundColor: "#E65300",
  color: "white",
  textAlign: "left",
  fontWeight: "bold",
  fontSize: "13px",
};

const tableCellStyle = {
  padding: "6px 8px",
  fontSize: "12.5px",
  borderBottom: "1px solid #ccc",
  height: "70px",  // increases row height
  overflowWrap: "break-word",  // handle long text

};


  return (

    <div
      style={{
        fontFamily: "Neuzeit Grotesk Regular, Ramaraj Regular" ,
        maxWidth: "1200px",
        margin: "0 auto",
        padding: "40px 20px",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        width: "100%",
      }}
    >

{/* Two-column layout */}
<div
  style={{
     width: "90%",           // increase from previous 60% or so
    maxWidth: "1200px",
    display: "flex",
    justifyContent: "center",
    gap: "80px",
    alignItems: "flex-start",
    marginBottom: "40px",
  }}
>
  {/*  International Courses */}
  <div
    style={{
      display: "flex",
      flexDirection: "column",
      gap: "10px",
      flex: 1,
      minHeight: "420px",
      minWidth: "400px"
    }}
  >  <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "left", fontFamily: "Archivo Black" }}>


      International Courses
    </h3>

   <label style={{ fontFamily: "Ramaraja Regular" }}>Country:</label>
   <select
  value={selectedCountry}
  onChange={(e) => setSelectedCountry(e.target.value)}
  style={dropdownStyle}
>
  <option value=""> </option>
 {countries
  .filter((c) => c && c.trim() !== "")
  .map((c, index) => (
    <option key={`${c}-${index}`} value={c}>
      {c}
    </option>
))}

</select>
    <label>University / Institution:</label>
<select
  value={selectedInstitution}
  onChange={(e) => setSelectedInstitution(e.target.value)}
  style={dropdownStyle}
>
  <option value=""> </option>
  {institutions
    .filter((i) => i && i.trim() !== "")
    .map((i, index) => (
      <option key={`${i}-${index}`} value={i.trim()}>
        {i.trim()}
      </option>
    ))}
</select>


  </div>

  {/* üéì UOP Equivalent Courses */}
  <div style={{ display: "flex", flexDirection: "column", gap: "10ps", flex: 1, minWidth: "400px", fontFamily: "Archivo Black", alignItems: "flex-start" }}>
  <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "left", fontFamily: "Archivo Black" }}>
    Pacific Equivalent Courses
  </h3>

    <label style={{marginTop: "20px",  marginBottom: "4px" }}>Majors:</label>
    <select
      value={selectedMajor}
      onChange={(e) => setSelectedMajor(e.target.value)}
      style={dropdownStyle}
    >
      <option value=""> </option>
      {uopSchools.map((s) => (
        <option key={s} value={s}>
          {s}
        </option>
      ))}
    </select>



    <label style={{ marginTop: "21px", marginBottom: "8px" }}>Course Code:</label>
    <input
      type="text"
      value={pacificCourseCode}
      onChange={(e) => setPacificCourseCode(e.target.value)}

      placeholder="e.g., HIST 120"
      style={{
      padding: "10px",
      fontSize: "16px",
      borderRadius: "6px",
      border: "1px solid #ccc",
      width: "100%",
      maxWidth: "480px",
      height: "40px",
      maxHeight: "60px",
      }}
    />
  </div>
