import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import UniversityAccordion from "./UniversityAccordion";

const CourseFilter = async () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [uopSchools, setUopSchools] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedMajor, setSelectedMajor] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredUniversities, setFilteredUniversities] = useState([]);
 const [noMatchMessage, setNoMatchMessage] = useState(""); 
 const COURSE_API = "https://api.sheety.co/96a991dd39be20e53e03f451852d0c0f/studyAbroadApprovedCourses/general";
const MAJOR_API = "https://api.sheety.co/96a991dd39be20e53e03f451852d0c0f/studyAbroadApprovedCourses/majors"

  const isSearchDisabled = !(
    selectedCountry ||
    selectedInstitution ||
    selectedSubjectArea ||
    selectedMajor ||
    selectedPacificSubjectArea ||
    pacificCourseCode );

  const [searchClicked, setSearchClicked] = useState(false);
  const [showResults, setShowResults] = useState(false);

  const dropdownStyle = {
  width: "90%",
  maxWidth: "400px",

  padding: "14px",
  borderRadius: "6px",
  border: "1px solid #ccc",
  fontSize: "17px",
  lineHeight: "1.6",
  marginBottom: "20px",
  backgroundColor: "#fff",
  backgroundImage: `url("data:image/svg+xml;utf8,<svg fill='%23232323' height='24' viewBox='0 0 24 24' width='24' xmlns='http://www.w3.org/2000/svg'><path d='M7 10l5 5 5-5z'/></svg>")`,
  backgroundRepeat: "no-repeat",
  backgroundPosition: "right 12px center",
  backgroundSize: "16px",
  appearance: "none",        
  WebkitAppearance: "none",
  backgroundColor: "#e6ebf1",
  border: "1px solid #bbb",
  color: "#333",

  };

  const handleClear = () => {
    setSelectedCountry("");
    setSelectedInstitution("");
    setSelectedSubjectArea("");
    setSelectedMajor("");
    setSelectedPacificSubjectArea("");
    setPacificCourseCode("");
    setResults([]);
    setShowTooltip(false);
  };

  // For general courses
const response = await fetch(COURSE_API);
const data = await response.json();
setAllCourses(data.general);

// For majors
const res = await fetch(MAJOR_API);
const majorsData = await res.json();
setUopSchools(majorsData.majors);


useEffect(() => {
  const loadData = async () => {
    try {
      // 1) Majors
      const uopMajorsRes = await fetch(
        "https://api.sheety.co/96a991dd39be20e53e03f451852d0c0f/studyAbroadApprovedCourses/majors"
      );
      const uopMajorsData = await uopMajorsRes.json();
      setUopSchools(Array.isArray(uopMajorsData.majors) ? uopMajorsData.majors : []);
      console.log("Majors:", uopMajorsData);

      // 2) Courses (Sheet tab: "general" or "generals")
      const coursesRes = await fetch(
        "https://api.sheety.co/96a991dd39be20e53e03f451852d0c0f/studyAbroadApprovedCourses/general"
      );
      const coursesData = await coursesRes.json();
      console.log("Courses raw:", coursesData);

      // Safely pick the array of rows
      const rows =
        (Array.isArray(coursesData.general) && coursesData.general) ||
        (Array.isArray(coursesData.generals) && coursesData.generals) ||
        [];

      // Optional: normalize column names coming from Sheety
      const norm = (r) => ({
        partner_university:
          r.partner_university ?? r.partnerUniversity ?? r.partnerUniversityName ?? "",
        country: r.country ?? r.Country ?? "",
        providers: r.providers ?? r.provider ?? r.Providers ?? "",
        // keep the rest of the fields
        ...r,
      });

      const normalized = rows.map(norm);

      // 3) Group by university + country
      const grouped = {};
      normalized.forEach((row) => {
        const key = `${row.partner_university}_${row.country}`;
        if (!grouped[key]) {
          grouped[key] = {
            partner_university: row.partner_university,
            country: row.country,
            courses: [],
            providersSet: new Set(),
          };
        }
        grouped[key].courses.push(row);
        if (row.providers) grouped[key].providersSet.add(row.providers);
      });

      // 4) Keep only universities with > 1 course
      const universitiesWithCourses = Object.values(grouped)
        .filter((uni) => uni.courses.length > 1)
        .map((uni) => ({
          ...uni,
          providers: Array.from(uni.providersSet),
        }));

      setFilteredUniversities(universitiesWithCourses);

      // 5) Flatten courses for your table/list
      const flatCourses = universitiesWithCourses.flatMap((uni) =>
        uni.courses.map((course) => ({
          ...course,
          partner_university: uni.partner_university,
          country: uni.country,
        }))
      );
      setAllCourses(flatCourses);

      // 6) Dropdowns
      const uniqueCountries = [...new Set(universitiesWithCourses.map((u) => u.country))].sort();
      const uniqueInstitutions = [...new Set(universitiesWithCourses.map((u) => u.partner_university))].sort();
      setCountries(uniqueCountries);
      setInstitutions(uniqueInstitutions);
    } catch (error) {
      console.error("Error loading data:", error);
    } finally {
      setLoading(false);
    }
  };

  loadData();
}, []);


const handleFilter = () => {
  const filtered = allCourses.filter((course) => {
    return (
    (!selectedCountry || (course.country || "").trim().toLowerCase() === selectedCountry.trim().toLowerCase()) &&
    (!selectedInstitution || (course.partner_university || "").trim() === selectedInstitution.trim()) &&
