import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import ExportButton from "./ExportButton";
import db from "../firebase";
import { collection, getDocs } from "firebase/firestore";

const dropdownStyle = {
  width: "100%",
  maxWidth: "300px",
  padding: "14px",
  marginBottom: "20px",
  borderRadius: "6px",
  border: "1px solid #ccc",
  fontSize: "17px",      
  lineHeight: "1.6",
   margin: "0 auto",  
    paddingTop: "50px", 

};

const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [uopSchools, setUopSchools] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);

  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedSchool, setSelectedSchool] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");
const [allCourses, setAllCourses] = useState([]);

  useEffect(() => {
  const fetchCountries = async () => {
    try {
      const response = await fetch(
        "https://gist.githubusercontent.com/farheen-shaikh530/a5205c7f0ddfc324beac5c65dfce7e80/raw/b60a9500ffad11cd46105f2add2e31a0c5111a4d/countries.json"
      );
      const data = await response.json();
      setCountries(data.countries);
    } catch (error) {
      console.error("Error fetching countries:", error);
    }
  };
const handleClear = () => {
  setSelectedCountry("");
  setSelectedInstitution("");
  setSelectedSubjectArea("");
  setSelectedSchool("");
  setSelectedPacificSubjectArea("");
  setPacificCourseCode("");
  setResults([]);
  setShowTooltip(false);
};


    const fetchUopSchools = async () => {
      try {
        const response = await fetch(
          "https://gist.githubusercontent.com/farheen-shaikh530/babe5bf8a55e7648e6c5176ce3de2860/raw/"
        );
        const data = await response.json();
        setUopSchools(data.schools);
        console.log("UOP Schools fetched:", data.schools);
      } catch (error) {
        console.error("Error fetching UOP schools:", error);
      }
    };

    const fetchCourseEquivalents = async () => {
  try {
    const response = await fetch("https://gist.githubusercontent.com/farheen-shaikh530/714ed972644392cff226383f5de66b66/raw/a6733c1411a089a67d5f81973f27e4860cecdf77/International_Course_Equivalent.json");
    const data = await response.json();
    setAllCourses(data.courses); // Make sure your JSON has { "courses": [...] }
  } catch (error) {
    console.error("Error fetching course equivalents:", error);
  }
};
fetchCourseEquivalents();


    const fetchPacificSubjects = async () => {
      try {
        const response = await fetch(
          "https://gist.githubusercontent.com/farheen-shaikh530/22fbd4686a5cd14cc92781038b0f8cfa/raw/"
        );
        const data = await response.json();
        setPacificSubjectAreas(data.subjects);
      } catch (error) {
        console.error("Error fetching Pacific subject areas:", error);
      }
    };

    const fetchFirebaseData = async () => {
      try {
        const snapshot = await getDocs(collection(db, "Courses"));
        const docs = snapshot.docs.map((doc) => doc.data());

        const uniqueInstitutions = [
          ...new Set(
            docs
              .map((doc) => doc.partner_university?.trim().toLowerCase())
              .filter(Boolean)
          ),
        ].map((name) => name.charAt(0).toUpperCase() + name.slice(1));

        const uniqueSubjects = [
          ...new Set(docs.map((doc) => doc.pacific_course_name)),
        ];

        setInstitutions(uniqueInstitutions.sort());
        setSubjectAreas(uniqueSubjects.sort());
      } catch (error) {
        console.error("Error fetching Firebase data:", error);
      }
    };

   fetchCountries();
    fetchUopSchools();
    fetchPacificSubjects();
    fetchFirebaseData();
  }, []);

const handleFilter = () => {
  const filtered = allCourses.filter((course) => {
    return (
      (!selectedCountry || course.country === selectedCountry) &&
      (!selectedInstitution || course.partner_university === selectedInstitution) &&
      (!selectedSubjectArea || course.partner_course_subject === selectedSubjectArea) &&
      (!selectedSchool || course.pacific_school === selectedSchool) &&
      (!selectedPacificSubjectArea || course.pacific_subject_area === selectedPacificSubjectArea) &&
      (!pacificCourseCode || course.pacific_course_code.toLowerCase().includes(pacificCourseCode.toLowerCase()))
    );
  });

  setResults(filtered);
};

  return (
<div
  style={{
   fontFamily: "Neuzeit Grotesk, sans-serif",
    maxWidth: "1200px",
    margin: "0 auto", // ‚úÖ Center horizontally
    padding: "40px 20px",
    minHeight: "60px", // ‚úÖ Full height
    display: "flex",
    flexDirection: "column",
    justifyContent: "center", // ‚úÖ Center vertically
    alignItems: "center",     // ‚úÖ Center horizontally
  }}
>
      {/* Two-column layout */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "80px",
          alignItems: "flex-start",
          marginBottom: "40px",

        }}
      >
        {/* üåê International Courses */}
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "10px",
            flex: 100,
            minHeight: "420px",
          }}
        >
          <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "center" }}>
            International Courses
          </h3>

          <label>Country:</label>
          <select
            value={selectedCountry}
            onChange={(e) => setSelectedCountry(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select Country</option>
            {countries.map((c) => (
              <option key={c} value={c}>
                {c}
              </option>
            ))}
          </select>

          <label>University / Institution:</label>
          <select
            value={selectedInstitution}
            onChange={(e) => setSelectedInstitution(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select Institution</option>
            {institutions.map((i) => (
              <option key={i} value={i}>
                {i}
              </option>
            ))}
          </select>

          <label>Subject Area:</label>
          <select
            value={selectedSubjectArea}
            onChange={(e) => setSelectedSubjectArea(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select Subject Area</option>
            {subjectAreas.map((s) => (
              <option key={s} value={s}>
                {s}
              </option>
            ))}
          </select>
        </div>

        {/* üéì UOP Equivalent Courses */}
        <div style={{ display: "flex", flexDirection: "column", gap: "10px", flex: 100 }}>
          <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "center" }}>
            UOP Equivalent
          </h3>

          <label>School:</label>
          <select
            value={selectedSchool}
            onChange={(e) => setSelectedSchool(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select UOP School</option>
            {uopSchools.map((s) => (
              <option key={s} value={s}>
                {s}
              </option>
            ))}
          </select>

<label style={{ marginBottom: "5px"}}>Pacific Subject Area:</label>
        <select
  value={selectedPacificSubjectArea}
  onChange={(e) => setSelectedPacificSubjectArea(e.target.value)}
  style={dropdownStyle}
>
  <option value="">Select Subject Area</option>
  {pacificSubjectAreas.map((s) => (
    <option key={s} value={s}>{s}</option>
  ))}
</select>

        <label style={{ marginBottom: "5px"}}>Course Code:</label>
          <input
          type="text"
          value={pacificCourseCode}
          onChange={(e) => setPacificCourseCode(e.target.value)}
          placeholder="e.g., HIST 120"
          style={{
            height: "40px",
            fontSize: "14px",
            padding: "8px 10px",
            width: "275px",// ‚¨ÖÔ∏è Set fixed width (adjust as needed)
            height:"10px",
  }}
/>      
        </div>
      </div>

      {/* üîç Buttons */}
      <div style={{ display: "flex", justifyContent: "center", gap: "20px", marginBottom: "0px",alignItems: "flex-start", }}>
        <button
          onClick={handleFilter}
          onMouseEnter={() => setShowTooltip(true)}
          onMouseLeave={() => setShowTooltip(false)}
          style={{
            backgroundColor: "#FF671D",
            color: "#fff",
            border: "none",
            padding: "10px 28px",
            fontWeight: "bold",
            fontSize: "16px",
            borderRadius: "6px",
            width: "120px",
            cursor: "pointer",
          }}
        >
          Search
        </button>

        <button
          onClick={handleClear}
          style={{
            backgroundColor: "#A29889",
            color: "#fff",
            border: "none",
            padding: "10px 28px",
            fontWeight: "bold",
            fontSize: "16px",
            borderRadius: "6px",
            width: "120px",
            cursor: "pointer",
          }}
        >
          Clear
        </button>
      </div>

      {/* üìä Table + Export */}
      <ResultsTable results={results} />
      <ExportButton results={results} />
    </div>
  );
};

