import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import ExportButton from "./ExportButton";
import db from "../firebase";
import { collection, getDocs } from "firebase/firestore";

const dropdownStyle = {
  width: "100%",
  maxWidth: "300px",
  padding: "10px",
  marginBottom: "20px",
  borderRadius: "5px",
  border: "1px solid #ccc",
  fontSize: "15px",
  lineHeight: "1.5",
};

const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedSchool, setSelectedSchool] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");

  const fetchPacificSubjects = async () => {
  try {
    const response = await fetch("https://gist.githubusercontent.com/farheen-shaikh530/22fbd4686a5cd14cc92781038b0f8cfa/raw/6c1e35e67e0f7de725ce7edde3c19b280fb3cbf3/UOP_Subjects.json"); // üëà Replace with your real API
    const data = await response.json();
    setPacificSubjectAreas(data.subjects); // Assuming JSON shape: { "subjects": ["Math", "CS", "Bio"] }
  } catch (error) {
    console.error("Error fetching Pacific subject areas:", error);
  }
};

useEffect(() => {
const fetchCountries = async () => {
  try {
    const response = await fetch("https://gist.githubusercontent.com/farheen-shaikh530/a5205c7f0ddfc324beac5c65dfce7e80/raw/b60a9500ffad11cd46105f2add2e31a0c5111a4d/countries.json");
    const data = await response.json();
    setCountries(data.countries); // ‚úÖ your JSON uses { "countries": [...] }
    console.log("Countries fetched:", data.countries);
  } catch (error) {
    console.error("Error fetching countries:", error);
  }
};

const fetchUopSchools = async () => {
  try {
    const response = await fetch("https://gist.githubusercontent.com/farheen-shaikh530/babe5bf8a55e7648e6c5176ce3de2860/raw/ca640e9a7cd13cd7b26f18019f6ea317dbfd2ce2/uop_schools.json");
    const data = await response.json();
    setUopSchools(data.schools);  // Must match your JSON key
    console.log("UOP Schools fetched:", data.schools);
  } catch (error) {
    console.error("Error fetching UOP schools:", error);
  }
};


  const fetchFirebaseData = async () => {
    try {
      const snapshot = await getDocs(collection(db, "Courses"));
      const docs = snapshot.docs.map((doc) => doc.data());

      const uniqueInstitutions = [
        ...new Set(docs.map((doc) => doc.partner_university?.trim().toLowerCase()).filter(Boolean)),
      ].map((name) => name.charAt(0).toUpperCase() + name.slice(1));

      const uniqueSubjects = [...new Set(docs.map((doc) => doc.pacific_course_name))];

      setInstitutions(uniqueInstitutions.sort());
      setSubjectAreas(uniqueSubjects.sort());
    } catch (error) {
      console.error("Error fetching data from Firebase:", error);
    }
  };



  // üöÄ Call all three once
  fetchCountries();
  fetchFirebaseData();
  fetchPacificSubjects();
}, []);


    const fetchFirebaseData = async () => {
      const snapshot = await getDocs(collection(db, "Courses"));
      const docs = snapshot.docs.map((doc) => doc.data());

      const uniqueInstitutions = [
        ...new Set(docs.map((doc) => doc.partner_university?.trim().toLowerCase()).filter(Boolean)),
      ].map((name) => name.charAt(0).toUpperCase() + name.slice(1));

      const uniqueSubjects = [...new Set(docs.map((doc) => doc.pacific_course_name))];

      setInstitutions(uniqueInstitutions.sort());
      setSubjectAreas(uniqueSubjects.sort());
    };




  const handleFilter = () => {
    console.log("Search clicked");
  };

  const handleClear = () => {
    setSelectedCountry("");
    setSelectedInstitution("");
    setSelectedSubjectArea("");
    setSelectedSchool("");
    setSelectedPacificSubjectArea("");
    setPacificCourseCode("");
    setResults([]);
    setShowTooltip(false);
  };

  return (
    <div
      style={{
        fontFamily: "Neuzeit Grotesk, sans-serif",
        maxWidth: "1200px",
        margin: "40px auto",
        padding: "20px",
      }}
    >
      {/* Two-column layout */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "80px",
          alignItems: "flex-start",
          marginBottom: "40px",
        }}
      >
        {/* üåê International Courses */}
        <div style={{ display: "flex", flexDirection: "column", gap: "10px", flex: 100, minHeight: "420px" }}>

          <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "center" }}>
            International Courses
          </h3>

          <label style={{ marginBottom: "5px"}}>Country:</label>
          <select
            value={selectedCountry}
            onChange={(e) => setSelectedCountry(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select Country</option>
            {countries.map((c) => (
              <option key={c} value={c}>{c}</option>
            ))}
          </select>

         <label style={{ marginBottom: "5px"}}>University / Institution:</label>
          <select
            value={selectedInstitution}
            onChange={(e) => setSelectedInstitution(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select Institution</option>
            {institutions.map((u) => (
              <option key={u} value={u}>{u}</option>
            ))}
          </select>

         <label style={{ marginBottom: "5px"}}>Subject Area:</label>
          <select
            value={selectedSubjectArea}
            onChange={(e) => setSelectedSubjectArea(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select Subject Area</option>
            {subjectAreas.map((s) => (
              <option key={s} value={s}>{s}</option>
            ))}
          </select>
        </div>

<div style={{ display: "flex", justifyContent: "center", alignItems: "center", gap: "20px", marginBottom: "30px" }}>

</div>
        {/* üéì UOP Equivalent Courses */}
        <div style={{ display: "flex", flexDirection: "column", gap: "10px", flex: 100 }}>
          <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "center" }}>
            UOP Equivalent Courses
          </h3>

          <label style={{ marginBottom: "5px" }}>School:</label>
          <select
            value={selectedSchool}
            onChange={(e) => setSelectedSchool(e.target.value)}
            style={dropdownStyle}
          >
            <option value="">Select UOP School</option>

          </select>

<label style={{ marginBottom: "5px"}}>Pacific Subject Area:</label>
        <select
  value={selectedPacificSubjectArea}
  onChange={(e) => setSelectedPacificSubjectArea(e.target.value)}
  style={dropdownStyle}
>
  <option value="">Select Subject Area</option>
  {pacificSubjectAreas.map((s) => (
    <option key={s} value={s}>{s}</option>
  ))}
</select>

        <label style={{ marginBottom: "5px"}}>Course Code:</label>
          <input
            type="text"
            value={pacificCourseCode}
            onChange={(e) => setPacificCourseCode(e.target.value)}
            placeholder="e.g., HIST 120"
            style={dropdownStyle}
          />
        </div>
      </div>

      {/* üîç Buttons */}
      <div style={{ display: "flex", justifyContent: "center", gap: "20px", marginBottom: "30px" }}>
        <button
          onClick={handleFilter}
          onMouseEnter={() => setShowTooltip(true)}
          onMouseLeave={() => setShowTooltip(false)}
          style={{
            backgroundColor: "#FF671D",
            color: "#fff",
            border: "none",
            padding: "10px 28px",
            fontWeight: "bold",
            fontSize: "16px",
            borderRadius: "6px",
            width: "120px",
            cursor: "pointer",
          }}
        >
          Search
        </button>

        <button
          onClick={handleClear}
          style={{
            backgroundColor: "#A29889",
            color: "#fff",
            border: "none",
            padding: "10px 28px",
            fontWeight: "bold",
            fontSize: "16px",
            borderRadius: "6px",
            width: "120px",
            cursor: "pointer",
          }}
        >
          Clear
        </button>
      </div>

      {/* üìä Table + Export */}
      <ResultsTable results={results} />
      <ExportButton results={results} />
    </div>
  );
};

export default CourseFilter;
