import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import ExportButton from "./ExportButton";
import UniversityAccordion from "./UniversityAccordion";



const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]);
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]);
  const [uopSchools, setUopSchools] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedSchool, setSelectedSchool] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filteredUniversities, setFilteredUniversities] = useState([]);

  const dropdownStyle = {
    width: "100%",
    maxWidth: "300px",
    padding: "14px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    fontSize: "17px",
    lineHeight: "1.6",
    margin: "0 auto 20px",
  };

  const handleClear = () => {
    setSelectedCountry("");
    setSelectedInstitution("");
    setSelectedSubjectArea("");
    setSelectedSchool("");
    setSelectedPacificSubjectArea("");
    setPacificCourseCode("");
    setResults([]);
    setShowTooltip(false);
  };

  // Fetch all static JSON data from Gists
  useEffect(() => {
  const loadAll = async () => {
    try {
      const schoolsRes = await fetch(
        "https://gist.githubusercontent.com/farheen-shaikh530/babe5bf8a55e7648e6c5176ce3de2860/raw/"
      );
      const pacificSubjectsRes = await fetch(
        "https://gist.githubusercontent.com/farheen-shaikh530/22fbd4686a5cd14cc92781038b0f8cfa/raw/"
      );
      const equivalentsRes = await fetch(
        "https://gist.githubusercontent.com/farheen-shaikh530/714ed972644392cff226383f5de66b66/raw/dd67d771177615fc54f0833660d3b337274bcfb1/international_universities.json"
      );

      const schoolsData = await schoolsRes.json();
      const pacificSubjectsData = await pacificSubjectsRes.json();
      const courseEquivalentsData = await equivalentsRes.json();

      setUopSchools(schoolsData.schools);
      setPacificSubjectAreas(pacificSubjectsData.subjects);
      setAllCourses(courseEquivalentsData.universities); // Use universities directly

      // Prepare dropdowns
      const uniqueCountries = [
        ...new Set(courseEquivalentsData.universities.map((c) => c.country)),
      ];
      const uniqueInstitutions = [
        ...new Set(courseEquivalentsData.universities.map((c) => c.partner_university)),
      ];

      setCountries(uniqueCountries.sort());
      setInstitutions(uniqueInstitutions.sort());
    } catch (error) {
      console.error("Error loading course data:", error);
    } finally {
      setLoading(false);
    }
  };

  loadAll();
}, []);

useEffect(() => {
  if (selectedCountry && allCourses.length > 0) {
    const matchedUnis = allCourses
      .filter((course) => course.country === selectedCountry)
      .map((course) => ({
        partner_university: course.partner_university,
        country: course.country,
      }));

    const uniqueUnis = Array.from(
      new Set(matchedUnis.map((u) => JSON.stringify(u)))
    ).map((u) => JSON.parse(u));

    setFilteredUniversities(uniqueUnis);
  } else {
    setFilteredUniversities([]);
  }
}, [selectedCountry, allCourses]);


  const handleFilter = () => {
    const filtered = allCourses.filter((course) => {
      return (
        (!selectedCountry || course.country === selectedCountry) &&
        (!selectedInstitution || course.partner_university === selectedInstitution) &&
        (!selectedSubjectArea || course.partner_course_subject === selectedSubjectArea) &&
        (!selectedSchool || course.pacific_school === selectedSchool) &&
        (!selectedPacificSubjectArea || course.pacific_subject_area === selectedPacificSubjectArea) &&
        (!pacificCourseCode || course.pacific_course_code.toLowerCase().includes(pacificCourseCode.toLowerCase()))
      );
    });
    setResults(filtered);
  };

  const tableHeaderStyle = {
  padding: "10px",
  backgroundColor: "#Ff671d",
  color: "white",
  textAlign: "left",
  fontWeight: "bold",
};

const tableCellStyle = {
  padding: "10px",
  borderBottom: "1px solid #ccc",
};


  return (


    <div
      style={{
        fontFamily: "Neuzeit Grotesk Regular, Ramaraj Regular" ,
        maxWidth: "1200px",
        margin: "0 auto",
        padding: "40px 20px",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
      }}
    >

{/* Two-column layout */}
<div
  style={{
    display: "flex",
    justifyContent: "center",
    gap: "80px",
    alignItems: "flex-start",
    marginBottom: "40px",
  }}
>
  {/* üåê International Courses */}
  <div
    style={{
      display: "flex",
      flexDirection: "column",
      gap: "10px",
      flex: 100,
      minHeight: "420px",
    }}
  >
    <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "center" }}>
      International Courses
    </h3>

    <label>Country:</label>
   <select
  value={selectedCountry}
  onChange={(e) => setSelectedCountry(e.target.value)}
  style={dropdownStyle}
>
  <option value="">Select Country</option>
 {countries
  .filter((c) => c && c.trim() !== "")
  .map((c, index) => (
    <option key={`${c}-${index}`} value={c}>
      {c}
    </option>
))}

</select>


    <label>University / Institution:</label>
    <select
  value={selectedInstitution}
  onChange={(e) => setSelectedInstitution(e.target.value)}
  style={dropdownStyle}
>
  <option value="">Select Institution</option>
 {institutions
  .filter((i) => i && i.trim() !== "")
  .map((i, index) => (
    <option key={`${i}-${index}`} value={i}>
      {i}
    </option>
))}
</select>

   <label>Subject Area:</label>
<select
  value={selectedSubjectArea}
  onChange={(e) => setSelectedSubjectArea(e.target.value)}
  style={dropdownStyle}
>
  <option value="">Select Subject Area</option>
  {subjectAreas
    .filter((s) => s && s.trim() !== "") // remove empty/null values
    .map((s, index) => (
      <option key={`${s}-${index}`} value={s}>
        {s}
      </option>
    ))}
</select>

  </div>

  {/* üéì UOP Equivalent Courses */}
  <div style={{ display: "flex", flexDirection: "column", gap: "10px", flex: 100 }}>
    <h3 style={{ fontSize: "26px", marginBottom: "20px", textAlign: "center" }}>
      UOP Equivalent
    </h3>

    <label>School:</label>
    <select
      value={selectedSchool}
      onChange={(e) => setSelectedSchool(e.target.value)}
      style={dropdownStyle}
    >
      <option value="">Select UOP School</option>
      {uopSchools.map((s) => (
        <option key={s} value={s}>
          {s}
        </option>
      ))}
    </select>

    <label style={{ marginBottom: "5px" }}>Pacific Subject Area:</label>
    <select
      value={selectedPacificSubjectArea}
      onChange={(e) => setSelectedPacificSubjectArea(e.target.value)}
      style={dropdownStyle}
    >
      <option value="">Select Subject Area</option>
      {pacificSubjectAreas.map((s) => (
        <option key={s} value={s}>{s}</option>
      ))}
    </select>

    <label style={{ marginBottom: "5px" }}>Course Code:</label>
    <input
      type="text"
      value={pacificCourseCode}
      onChange={(e) => setPacificCourseCode(e.target.value)}
      placeholder="e.g., HIST 120"
      style={{
        height: "40px",
        fontSize: "14px",
        padding: "8px 10px",
        width: "275px",
      }}
    />
  </div>
</div>
  {/* üîç Buttons */}

<div style={{ display: "flex", justifyContent: "center", gap: "20px", marginBottom: "0px", alignItems: "flex-start" }}>
    <button
      onClick={handleFilter}
      onMouseEnter={() => setShowTooltip(true)}
      onMouseLeave={() => setShowTooltip(false)}
      style={{
        backgroundColor: "#FF671D",
        color: "#fff",
        border: "none",
        padding: "10px 28px",
        fontWeight: "bold",
        fontSize: "16px",
        borderRadius: "6px",
        width: "120px",
        cursor: "pointer",
      }}  
    >
      Search
    </button>

    <button
      onClick={handleClear}
      style={{
        backgroundColor: "#A29889",
        color: "#fff",
        border: "none",
        padding: "10px 28px",
        fontWeight: "bold",
        fontSize: "16px",
        borderRadius: "6px",
        width: "120px",
        cursor: "pointer",
      }}
    >
      Clear
    </button>
  </div>

  {/* Table + Export */}

{selectedCountry && filteredUniversities.length > 0 && (
  <UniversityAccordion universities={filteredUniversities} />
)}


  <ExportButton results={results} />
</div> 

  );
};

export default CourseFilter;
