import React, { useState, useEffect } from "react";
import ResultsTable from "./ResultsTable";
import UniversityAccordion from "./UniversityAccordion";

const COURSE_API =
  "https://api.sheety.co/96a991dd39be20e53e03f451852d0c0f/studyAbroadApprovedCourses/general";
const MAJOR_API =
  "https://api.sheety.co/96a991dd39be20e53e03f451852d0c0f/studyAbroadApprovedCourses/majors";


const CourseFilter = () => {
  const [countries, setCountries] = useState([]);
  const [institutions, setInstitutions] = useState([]);
  const [subjectAreas, setSubjectAreas] = useState([]); // if you use later
  const [pacificCourseCode, setPacificCourseCode] = useState("");
  const [pacificSubjectAreas, setPacificSubjectAreas] = useState([]); // if you use later
  const [uopSchools, setUopSchools] = useState([]);
  const [results, setResults] = useState([]);
  const [showTooltip, setShowTooltip] = useState(false);
  const [selectedCountry, setSelectedCountry] = useState("");
  const [selectedInstitution, setSelectedInstitution] = useState("");
  const [selectedSubjectArea, setSelectedSubjectArea] = useState("");
  const [selectedMajor, setSelectedMajor] = useState("");
  const [selectedPacificSubjectArea, setSelectedPacificSubjectArea] = useState("");
  const [allCourses, setAllCourses] = useState([]);
  const [loading, setLoading] = useState(true);
  const [noMatchMessage, setNoMatchMessage] = useState("");
  const [showResults, setShowResults] = useState(false);

  const isSearchDisabled = !(
    selectedCountry ||
    selectedInstitution ||
    selectedSubjectArea ||
    selectedMajor ||
    selectedPacificSubjectArea ||
    pacificCourseCode
  );

  //  Fetch data ONCE here (no top-level await!)
  useEffect(() => {
    const load = async () => {
      try {
        // courses
        const resp = await fetch(COURSE_API);
        const data = await resp.json();


      // Sheety tab might be "general" or "generals" (or weird cap)

       const rows = Array.isArray(data.general)
        ? data.general
        : Array.isArray(data.generals)
        ? data.generals
        : [];
         const normalized = rows.map((r) => {
        const pacificNameRaw =
          r.pacific_course_name ??
          r.pacificCourseName ??
          r['"pacific_course_name\n"'] ?? // your weird header
          r['"pacific_course_name"'] ??
          "";
           return {
          partner_university:
            sstr(r.partner_university ?? r.partnerUniversity ?? r["partner_university"] ?? r["partner university"]).trim(),
          country: sstr(r.country ?? r.Country).trim(),
          partner_course_code:
            sstr(r.partner_course_code ?? r.partnerCourseCode ?? r["partner course code"]).trim(),
          partner_course_name:
            sstr(r.partner_course_name ?? r.partnerCourseName ?? r["partner course name"]).trim(),
          partner_course_credit:
            sstr(
              r.partner_course_credit ??
              r.partnerCourseCredit ??
              r.Partner_course_credit // typo in your sheet
            ).trim(),
          pacific_course_code:
            sstr(r.pacific_course_code ?? r.pacificCourseCode ?? r["pacific course code"]).trim(),
          pacific_course_name: sstr(pacificNameRaw).replace(/"/g, "").trim(),
          pacific_major: sstr(r.pacific_major ?? r.pacificMajor ?? r["pacific major"]).trim(),
          pacific_course_credit:
            sstr(r.pacific_course_credit ?? r.pacificCourseCredit ?? r["pacific course credit"]).trim(),
          approval_term: sstr(r.approval_term ?? r.approvalTerm ?? r["approval term"]).trim(),
          providers: sstr(r.providers ?? r.provider ?? r.Provider).trim(),
          // keep the raw record if you need to inspect later
          __raw: r,
        };
      });

 setAllCourses(normalized);

        setAllCourses(rows);  
        // build dropdowns from normalized fields
      setCountries(
        [...new Set(normalized.map((r) => r.country))].filter(Boolean).sort()
      );
      setInstitutions(
        [...new Set(normalized.map((r) => r.partner_university))].filter(Boolean).sort()
      );


        setUopSchools(Array.isArray(mData.major) ? mData.major : []);
       // set dropdowns safely
      setCountries(
        [...new Set(rows.map((r) => sstr(r.country)))]
          .map((x) => x.trim())
          .filter((x) => x.length > 0)
          .sort()
      );
      setInstitutions(
        [...new Set(rows.map((r) => sstr(r.partner_university)))]
          .map((x) => x.trim())
          .filter((x) => x.length > 0)
          .sort()
      );

       // --- majors (NORMALIZE HERE) ---
        const mResp = await fetch(MAJOR_API);
        const mData = await mResp.json();

        const majorsList = Array.isArray(mData.major)
        ? mData.major.map((m) => {
            // if API already gives strings, keep them
            if (typeof m === "string") return m;
            // otherwise try common field names
            return (
              m?.major ??
              m?.Major ??
              m?.name ??
              m?.Name ??
              ""
            );
          })
        : [];

      setUopSchools(majorsList.filter((x) => sstr(x).trim().length > 0));


      } catch (e) {
        console.error("Error loading data:", e);
      } finally {
        setLoading(false);
      }
    };
    load();
  }, []);

  const handleClear = () => {
    setSelectedCountry("");
    setSelectedInstitution("");
    setSelectedSubjectArea("");
    setSelectedMajor("");
    setSelectedPacificSubjectArea("");
    setPacificCourseCode("");
    setResults([]);
    setShowTooltip(false);
    setShowResults(false);
    setNoMatchMessage("");
  };
  const handleFilter = () => {
    const sstr = (v) => (v == null ? "" : String(v));


    const filtered = allCourses.filter((course) => {
      return (
        (!selectedCountry ||
          (course.country || "").trim().toLowerCase() ===
            selectedCountry.trim().toLowerCase()) &&
        (!selectedInstitution ||
          (course.partner_university || "").trim() === selectedInstitution.trim()) &&
        (!selectedSubjectArea ||
          (course.partner_course_subject || "") === selectedSubjectArea) &&
        (!selectedPacificSubjectArea ||
          (course.pacific_subject_area || "") === selectedPacificSubjectArea) &&
        (!pacificCourseCode ||
          (course.pacific_course_code || "")
            .toLowerCase()
            .includes(pacificCourseCode.toLowerCase())) &&
        (!selectedMajor ||
          (course.pacific_major || "").trim().toLowerCase() ===
            selectedMajor.trim().toLowerCase())
      );
    });

    const grouped = filtered.reduce((acc, course) => {
      const key = `${course.partner_university}__${course.country}`;
      if (!acc[key]) {
        acc[key] = {
          partner_university: course.partner_university,
          country: course.country,
          courses: [],
        };
      }
      acc[key].courses.push(course);
      return acc;
    }, {});

    const groupedArray = Object.values(grouped).map((uni) => {
      const providersSet = new Set();
      uni.courses.forEach((c) => c.providers && providersSet.add(c.providers));
      return { ...uni, providers: Array.from(providersSet) };
    });

    setResults(groupedArray);
    setShowResults(true);
    setNoMatchMessage(
      groupedArray.length === 0
        ? `No courses in ${selectedCountry || "selected country"} for the selected major.`
        : ""
    );
  };

  const dropdownStyle = {
    width: "90%",
    maxWidth: "400px",
    padding: "14px",
    borderRadius: "6px",
    border: "1px solid #ccc",
    fontSize: "17px",
    lineHeight: "1.6",
    marginBottom: "20px",
    backgroundColor: "#e6ebf1",
    backgroundRepeat: "no-repeat",
    backgroundPosition: "right 12px center",
    backgroundSize: "16px",
    appearance: "none",
    WebkitAppearance: "none",
    color: "#333",
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div
      style={{
        fontFamily: "Neuzeit Grotesk Regular, Ramaraj Regular",
        maxWidth: "1200px",
        margin: "0 auto",
        padding: "40px 20px",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        alignItems: "center",
        width: "100%",
      }}
    >
      {/* Two-column layout */}
      <div
        style={{
          width: "90%",
          maxWidth: "1200px",
          display: "flex",
          justifyContent: "center",
          gap: "80px",
          alignItems: "flex-start",
          marginBottom: "40px",
        }}
      >
        {/* Pacific Courses */}
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "10px",
            flex: 1,
            minHeight: "420px",
            minWidth: "400px",
          }}
        >
          <h3
            style={{
              fontSize: "26px",
              marginTop: "0px",
              marginBottom: "10px",
              textAlign: "left",
              fontFamily: "Bely Display",
            }}
          >
            Pacific Courses
          </h3>

          <label style={{ marginTop: "42px", marginBottom: "8px", fontFamily: "Ramaraja Regular" }}>
            Majors:
          </label>

         <select
          value={selectedMajor}
          onChange={(e) => setSelectedMajor(e.target.value)}
          style={dropdownStyle}
        >
          <option value=""> </option>
          {uopSchools
            .map((s) => sstr(s).trim())
            .filter((s) => s.length > 0)
            .map((s, i) => (
              <option key={`${s}-${i}`} value={s}>
                {s}
              </option>
            ))}
        </select>

          <label style={{ marginBottom: "8px", fontFamily: "Ramaraja Regular" }}>
            Course Code:
          </label>
          <input
            type="text"
            value={pacificCourseCode}
            onChange={(e) => setPacificCourseCode(e.target.value)}
            placeholder="e.g., HIST 120"
            style={{
              padding: "10px",
              fontSize: "14px",
              borderRadius: "6px",
              border: "1px solid #ccc",
              width: "76%",
              maxWidth: "400px",
              height: "35px",
              maxHeight: "50px",
              marginBottom: "20px",
              backgroundColor: "#f9f9f9",
            }}
          />
        </div>

        {/* International Course Equivalents */}
        <div
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "10px",
            flex: 1,
            minHeight: "420px",
            minWidth: "400px",
          }}
        >
          <h3
            style={{
              fontSize: "26px",
              marginTop: "0px",
              marginBottom: "10px",
              textAlign: "left",
              fontFamily: "Bely Display",
            }}
          >
            International Course Equivalents
          </h3>

          <label style={{ marginTop: "42px", marginBottom: "8px", fontFamily: "Ramaraja Regular" }}>
            Country:
          </label>
          <select
            value={selectedCountry}
            onChange={(e) => setSelectedCountry(e.target.value)}
            style={dropdownStyle}
          >
            <option value=""> </option>
            {countries
              .filter((c) => c && c.trim() !== "")
              .map((c, index) => (
                <option key={`${c}-${index}`} value={c}>
                  {c}
                </option>
              ))}
          </select>

          <label>University / Institution:</label>
          <select
            value={selectedInstitution}
            onChange={(e) => setSelectedInstitution(e.target.value)}
            style={dropdownStyle}
          >
            <option value=""> </option>
            {institutions
              .filter((i) => i && i.trim() !== "")
              .map((i, index) => (
                <option key={`${i}-${index}`} value={i.trim()}>
                  {i.trim()}
                </option>
              ))}
          </select>
        </div>
      </div>

      {/* Buttons */}
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          gap: "20px",
          marginBottom: "0px",
          alignItems: "flex-start",
        }}
      >
        <button
          onClick={handleFilter}
          disabled={isSearchDisabled}
          title="Please be more specific in your search"
          style={{
            backgroundColor: "#E65300",
            color: "#fff",
            border: "none",
            padding: "10px 28px",
            fontWeight: "bold",
            fontSize: "16px",
            borderRadius: "6px",
            width: "120px",
            cursor: isSearchDisabled ? "not-allowed" : "pointer",
            opacity: isSearchDisabled ? 0.6 : 1,
          }}
        >
          Search
        </button>

        <button
          onClick={handleClear}
          style={{
            backgroundColor: " #A29889",
            color: "#fff",
            border: "none",
            padding: "10px 28px",
            fontWeight: "bold",
            fontSize: "16px",
            borderRadius: "6px",
            width: "120px",
            cursor: "pointer",
          }}
        >
          Clear
        </button>
      </div>

      {showResults && results.length > 0 && (
        <UniversityAccordion universities={results} />
      )}

      {showResults && results.length === 0 && noMatchMessage && (
        <div
          style={{
            marginTop: "20px",
            padding: "14px",
            backgroundColor: "#f6f6f6",
            borderLeft: "4px solid #E65300",
            borderRadius: "4px",
            fontSize: "16px",
            color: "#333",
            width: "85%",
            fontFamily: "Neuzeit Grotesk Light",
          }}
        >
          {noMatchMessage}
        </div>
      )}
    </div>
  );
};
export default CourseFilter;
